#include "ace/Log_Msg_IPC.h"
#include "ace/Log_Record.h"
#include "ace/CDR_Stream.h"
#include "ace/Truncate.h"



ACE_BEGIN_VERSIONED_NAMESPACE_DECL

ACE_Log_Msg_IPC::ACE_Log_Msg_IPC (void)
{
}

ACE_Log_Msg_IPC::~ACE_Log_Msg_IPC (void)
{
  (void) this->close ();
}

int
ACE_Log_Msg_IPC::open (const ACE_TCHAR *logger_key)
{
  ACE_LOG_MSG_IPC_CONNECTOR con;
  return con.connect (this->message_queue_,
                      ACE_LOG_MSG_IPC_ADDR (logger_key));
}

int
ACE_Log_Msg_IPC::reset (void)
{
  if (this->message_queue_.get_handle () != ACE_INVALID_HANDLE)
    {
      // If we don't do this, handles aren't reused on Win32 and the
      // server eventually crashes!
      return this->close ();
    }
  return 0;
}

int
ACE_Log_Msg_IPC::close (void)
{
  return this->message_queue_.close ();
}

ssize_t
ACE_Log_Msg_IPC::log (ACE_Log_Record &log_record)
{
  // Serialize the log record using a CDR stream, allocate enough
  // space for the complete <ACE_Log_Record>.
  size_t const max_payload_size =
    4    // type
    + 4  // pid
    + 12 // timestamp
    + 4  // process id
    + 4  // data length
#if defined (ACE_USES_WCHAR)
    + (log_record.msg_data_len () * ACE_OutputCDR::wchar_maxbytes())  // message
#else
    + log_record.msg_data_len () // message
#endif
    + ACE_CDR::MAX_ALIGNMENT;     // padding;

  // Insert contents of <log_record> into payload stream.
  ACE_OutputCDR payload (max_payload_size);
  if (!(payload << log_record))
    return -1;

  // Get the number of bytes used by the CDR stream. If it becomes desireable
  // to support payloads more than 4GB, this field will need to be changed
  // to a 64-bit value.
  ACE_CDR::ULong const length =
    ACE_Utils::truncate_cast<ACE_CDR::ULong> (payload.total_length ());

  // Send a header so the receiver can determine the byte order and
  // size of the incoming CDR stream.
  ACE_OutputCDR header (ACE_CDR::MAX_ALIGNMENT + 8);
  if (!(header << ACE_OutputCDR::from_boolean (ACE_CDR_BYTE_ORDER)))
   return -1;

  // Store the size of the payload that follows
  if (!(header << ACE_CDR::ULong (length)))
   return -1;

  // Use an iovec to send both buffer and payload simultaneously.
  iovec iov[2];
  iov[0].iov_base = header.begin ()->rd_ptr ();
  iov[0].iov_len  = 8;
  iov[1].iov_base = payload.begin ()->rd_ptr ();
  iov[1].iov_len  = length;

#if (ACE_HAS_STREAM_LOG_MSG_IPC == 1)
  // Use the <putpmsg> API if supported to ensure correct message
  // queueing according to priority.

  ACE_Str_Buf header_msg (static_cast<void *> (header.begin ()->rd_ptr ()),
                          static_cast<int> (8));

  ACE_Str_Buf payload_msg (static_cast<void *> (payload.begin ()->rd_ptr ()),
                           static_cast<int> (length));

  return this->message_queue_.send (&header_msg,
                                    &payload_msg,
                                    static_cast<int> (log_record.priority ()),
                                    MSG_BAND);
#else
  // We're running over sockets, so send header and payload
  // efficiently using "gather-write".
  return this->message_queue_.sendv_n (iov, 2);
#endif /* ACE_HAS_STREAM_LOG_MSG_IPC */
}

ACE_END_VERSIONED_NAMESPACE_DECL

#include "Maze.h"
#include <set>
#include <vector>
#include <algorithm>

using namespace std;

CMaze::CMaze(int nWidth, int nHeight, int nCellWidth, int nCellHeight, bool bCopyScreen)
{
    m_nCounter = 0;

    m_nWidth = nWidth;
    m_nHeight = nHeight;
    m_nCellWidth = nCellWidth;
    m_nCellHeight = nCellHeight;
    m_nCols = m_nWidth / m_nCellWidth - 1;
    m_nRows = m_nHeight / m_nCellHeight - 1;
    m_nOffsetX = (m_nWidth - m_nCols * m_nCellWidth) / 2;
    m_nOffsetY = (m_nHeight - m_nRows * m_nCellHeight) / 2;

    m_bReDrawBeforeGenerate = FALSE;
    m_bAllowNonSolution = FALSE;
    m_bFlashPath = TRUE;

//     m_nOffsetX = 2;
//     m_nOffsetY = 2;

    HDC hScreenDc = GetDC(NULL);

    m_hWorkDc = CreateCompatibleDC(NULL);
    m_hWorkBmp = CreateCompatibleBitmap(hScreenDc, m_nWidth, m_nHeight);
    m_hOldBmp = SelectObject(m_hWorkDc, (HGDIOBJ)m_hWorkBmp);
    m_hOldPen = SelectObject(m_hWorkDc, m_mazeColor.GetPen(clWall));

    if (bCopyScreen)
    {
        BitBlt(m_hWorkDc, 0, 0, m_nWidth, m_nHeight, hScreenDc, 0, 0, SRCCOPY);
    }

    ReleaseDC(NULL, hScreenDc);

    RECT rect = {0, 0, m_nWidth, m_nHeight};
    FillRect(m_hWorkDc, &rect, m_mazeColor.GetBrush(clBackGround));

    if (m_nCols > 0 && m_nRows > 0)
    {
        m_cells = new CMazeCell[m_nCols * m_nRows];

        InitializeCells();
        ResetAndReDrawAllCells();
        m_state = MS_INITIALIZED;
    }
    else
    {
        m_state = MS_ERROR;
        m_cells = NULL;
    }
}

CMaze::~CMaze(void)
{
    SelectObject(m_hWorkDc, m_hOldPen);
    SelectObject(m_hWorkDc, m_hOldBmp);
    DeleteDC(m_hWorkDc);
    DeleteObject(m_hWorkBmp);

    if (m_cells != NULL)
    {
        delete []m_cells;
    }
}

CMazeCell *CMaze::GetCell(int i, int j)
{
    if (i < 0 || j < 0 || i >= m_nCols || j >= m_nRows)
    {
        return NULL;
    }

    return &m_cells[j * m_nCols + i];
}

void CMaze::InitializeCells()
{
    for (int i = 0; i < m_nCols; ++i)
    {
        for (int j = 0; j < m_nRows; ++j)
        {
            CMazeCell *pCell = GetCell(i, j);

            pCell->SetParent(this, i, j);
        }
    }
}

void CMaze::ReDrawSides()
{
    int nPicWidth = m_nCols * m_nCellWidth;
    int nPicHeight = m_nRows * m_nCellHeight;

    MoveToEx(m_hWorkDc, m_nOffsetX, m_nOffsetY, NULL);
    LineTo(m_hWorkDc, m_nOffsetX + nPicWidth, m_nOffsetY);
    LineTo(m_hWorkDc, m_nOffsetX + nPicWidth, m_nOffsetY + nPicHeight);
    LineTo(m_hWorkDc, m_nOffsetX, m_nOffsetY + nPicHeight);
    LineTo(m_hWorkDc, m_nOffsetX, m_nOffsetY);
}

void CMaze::ResetAllCells()
{
    for (int i = 0; i < m_nCols; ++i)
    {
        for (int j = 0; j < m_nRows; ++j)
        {
            CMazeCell *pCell = GetCell(i, j);

            pCell->Reset();
        }
    }
}

void CMaze::ResetAndReDrawAllCells()
{
    for (int i = 0; i < m_nCols; ++i)
    {
        for (int j = 0; j < m_nRows; ++j)
        {
            CMazeCell *pCell = GetCell(i, j);

            pCell->Reset();
            pCell->ReDraw();
        }
    }

    ReDrawSides();
}

void CMaze::ReDrawAllCells()
{
    for (int i = 0; i < m_nCols; ++i)
    {
        for (int j = 0; j < m_nRows; ++j)
        {
            CMazeCell *pCell = GetCell(i, j);

            pCell->ReDraw();
        }
    }

    ReDrawSides();
}

void CMaze::Next(BOOL bDelay)
{
    DWORD dwDelayValue = 0;

    switch (m_state)
    {
    case MS_INITIALIZED:
        m_cellGenerating = GetRandomCell();
        m_cellStack.Init();
        m_cellStack.Push(m_cellGenerating);
        m_cellGenerating->m_bGenerated = true;
        ChangeState(MS_GENERATING);
        break;

    case MS_GENERATING:
        if (m_cellStack.IsEmpty())
        {
            ChangeState(MS_GENERATED);
        }
        else
        {
            bool bGetNewCell = false;
            CMazeCell *pTopCell = m_cellStack.Top();

            pTopCell->ReDraw();
            pTopCell->GoGenerate(&bGetNewCell);

            if (bGetNewCell)
            {
                //dwDelayValue = 1;
            }
        }
        break;

    case MS_GENERATED:
        // 
        if (m_bAllowNonSolution && m_nRows * m_nCols >= 10 && m_nCols > 1 && m_nRows > 1)
        {
            int nRightWallsCount = (m_nCols - 1) * m_nRows;
            int nBottomWallsCount = (m_nRows - 1) * m_nCols;
            int nWallsCount = nRightWallsCount + nBottomWallsCount;
            int nChangedWallsCount = nWallsCount * 2 / 100;
            set<int> setChangedWalls;
            vector<int> vectorChangedWalls;

            if (nChangedWallsCount > 100)
            {
                // 
                nChangedWallsCount = 100;
            }

            while ((int)setChangedWalls.size() < nChangedWallsCount)
            {
                setChangedWalls.insert(BigRand() % nWallsCount);
            }

            vectorChangedWalls.assign(setChangedWalls.begin(), setChangedWalls.end());
            random_shuffle(vectorChangedWalls.begin(), vectorChangedWalls.end());

            for (int i = 0; i < nChangedWallsCount; ++i)
            {
                int nOffset = vectorChangedWalls.at(i);

                if (nOffset < nRightWallsCount)
                {
                    int nRow = nOffset / (m_nCols - 1);
                    int nCol = nOffset % (m_nCols - 1);
                    CMazeCell *pCell = GetCell(nCol, nRow);

                    pCell->SetWall(DIR_RIGHT, !pCell->GetWall(DIR_RIGHT));
                }
                else
                {
                    nOffset -= nRightWallsCount;

                    int nRow = nOffset / m_nCols;
                    int nCol = nOffset % m_nCols;
                    CMazeCell *pCell = GetCell(nCol, nRow);

                    pCell->SetWall(DIR_BOTTOM, !pCell->GetWall(DIR_BOTTOM));
                }
            }
        }
        ReDrawAllCells();
        ChangeState(MS_SETTING);
        break;

    case MS_SETTING:
        if (true)
        {
            int nCounter = GetCounter();

            if (nCounter == 0)
            {
                if (rand() % 5 == 0)
                {
                    int bx[] = {0, 0, 1, 1};
                    int by[] = {0, 1, 0, 1};
                    int ex[] = {1, 1, 0, 0};
                    int ey[] = {1, 0, 1, 0};
                    int offset = rand() % 4;

                    m_cellBegin = GetCell((GetCols() - 1) * bx[offset], (GetRows() - 1) * by[offset]);
                    m_cellEnd = GetCell((GetCols() - 1) * ex[offset], (GetRows() - 1) * ey[offset]);
                }
                else
                {
                    m_cellBegin = GetRandomCell();

                    do
                    {
                        m_cellEnd = GetRandomCell();

                    } while (m_cellEnd == m_cellBegin);
                }

                m_cellBegin->m_bIsBegin = true;
                m_cellEnd->m_bIsEnd = true;
            }
            else if (nCounter < 19)
            {
                m_cellBegin->m_bIsBegin = !m_cellBegin->m_bIsBegin;
                m_cellBegin->ReDraw();
                dwDelayValue = 55;
            }
            else if (nCounter < 37)
            {
                m_cellEnd->m_bIsEnd = !m_cellEnd->m_bIsEnd;
                m_cellEnd->ReDraw();
                dwDelayValue = 55;
            }
            else
            {
                ChangeState(MS_SET);
            }
        }
        break;

    case MS_SET:
        m_cellStack.Init();
        m_cellStack.Push(m_cellBegin);
        m_cellBegin->m_bVisited = true;
        ChangeState(MS_RESOLVING);
        break;

    case MS_RESOLVING:
        if (m_cellStack.IsEmpty())
        {
            ChangeState(MS_RESOLVEFAILED);
        }
        else
        {
            bool bGetNewCell = false;
            CMazeCell *pTopCell = m_cellStack.Top();

            pTopCell->ReDraw();

            if (pTopCell == m_cellEnd)
            {
                ChangeState(MS_RESOLVED);
                break;
            }

            pTopCell->GoResolve(&bGetNewCell);

            if (bGetNewCell)
            {
                //dwDelayValue = 1;
            }
        }
        dwDelayValue = 10;
        break;

    case MS_RESOLVED:
        {
            int nCounter = GetCounter();

            if (nCounter > 5)
            {
                if (m_bReDrawBeforeGenerate)
                {
                    ResetAndReDrawAllCells();
                }
                else
                {
                    ResetAllCells();
                }

                ChangeState(MS_INITIALIZED);
            }
            else
            {
                if (m_bFlashPath)
                {
                    COLORREF cl = m_mazeColor.GetColor(clVisited);

                    m_mazeColor.SetColor(clVisited, GetReverseColor(cl));

                    ReDrawAllCells();
                }

                dwDelayValue = 500;
            }
        }
        break;

    case MS_RESOLVEFAILED:
        dwDelayValue = 3000;
        if (m_bReDrawBeforeGenerate)
        {
            ResetAndReDrawAllCells();
        }
        else
        {
            ResetAllCells();
        }
        ChangeState(MS_INITIALIZED);

    case MS_PAUSED:
        break;

    case MS_ERROR:
        break;

    default:
        break;
    }

    if (bDelay && dwDelayValue > 0)
    {
        Sleep(dwDelayValue);
    }
}

