int CMaze::BigRand()
{
    return
        (((rand() % 0x100) << 0) |
        ((rand() % 0x100) << 8) |
        ((rand() % 0x100) << 16) |
        ((rand() % 0x100) << 24)) &
        ((unsigned)-1 >> 1);
}

COLORREF CMaze::GetReverseColor(COLORREF cl)
{
    return RGB(~GetRValue(cl), ~GetGValue(cl), ~GetBValue(cl));
}

