#include "DistanceStatComputer.hpp"
#include "Task/Stats/DistanceStat.hpp"

void
DistanceStatComputer::CalcSpeed(DistanceStat &data, fixed time)
{
  if (positive(time) && data.IsDefined())
    data.speed = data.GetDistance() / time;
  else
    data.speed = fixed(0);
}

