class TestArabicShapeThreads : public SimpleThread
{
public:
    TestArabicShapeThreads(char* whatToChange) { fWhatToChange = whatToChange;}
    virtual void run() {
	    if(doTailTest()==TRUE)
			*fWhatToChange = '*';
    }
private:
