void Interpreter::cmp(UGeckoInstruction _inst)
{
	s32 a = (s32)rGPR[_inst.RA];
	s32 b = (s32)rGPR[_inst.RB];
	int fTemp;

	if (a < b)
		fTemp = 0x8;
	else if (a > b)
		fTemp = 0x4;
	else // Equals
		fTemp = 0x2;

	if (GetXER_SO())
		fTemp |= 0x1;

	SetCRField(_inst.CRFD, fTemp);
}

