void Interpreter::cntlzwx(UGeckoInstruction _inst)
{
	u32 val = rGPR[_inst.RS];
	u32 mask = 0x80000000;

	int i = 0;
	for (; i < 32; i++, mask >>= 1)
	{
		if (val & mask)
			break;
	}

	rGPR[_inst.RA] = i;

	if (_inst.Rc)
		Helper_UpdateCR0(rGPR[_inst.RA]);
}

