void Interpreter::addex(UGeckoInstruction _inst)
{
	int carry = GetCarry();
	int a = rGPR[_inst.RA];
	int b = rGPR[_inst.RB];
	rGPR[_inst.RD] = a + b + carry;
	SetCarry(Helper_Carry(a, b) || (carry != 0 && Helper_Carry(a + b, carry)));

	if (_inst.OE)
		PanicAlert("OE: addex");

	if (_inst.Rc)
		Helper_UpdateCR0(rGPR[_inst.RD]);
}

