void Interpreter::divwx(UGeckoInstruction _inst)
{
	s32 a = rGPR[_inst.RA];
	s32 b = rGPR[_inst.RB];

	if (b == 0 || ((u32)a == 0x80000000 && b == -1))
	{
		if (_inst.OE)
		{
			// should set OV
			PanicAlert("OE: divwx");
		}

		if (((u32)a & 0x80000000) && b == 0)
			rGPR[_inst.RD] = -1;
		else
			rGPR[_inst.RD] = 0;
	}
	else
	{
		rGPR[_inst.RD] = (u32)(a / b);
	}

	if (_inst.Rc)
		Helper_UpdateCR0(rGPR[_inst.RD]);
}


