void isrHandler(ptRegs* regs){
    // printDecimal(regs->intNum,color_red);
    if(interruptHandler[regs->intNum])
        interruptHandler[regs->intNum](regs);
    else{
        // printStr("Handeler not exist!",color_lightwhite);
        // printNum(regs->rip,color_lightwhite);
    }
}

void irqHandler(ptRegs* regs){
    if(regs->intNum>40){
        writePort(0xA0,0x20);// 
    }
    // 
    writePort(0x20, 0x20);

    if (interruptHandler[regs->intNum]) {
        interruptHandler[regs->intNum](regs);
    }
}

void registerInterruptHandler(uint8 n,interruptHandler_t h){
    interruptHandler[n]=h;
}

void idtSetEntity(uint8 num,uint64 base,uint16 selector,uint8 flags){
    idtEntries[num].offset1=base&0xffff;
    idtEntries[num].offset2=(base>>16)&0xffff;
    idtEntries[num].offset3=(base>>32)&0xffffffff;
    idtEntries[num].selector=selector;
    idtEntries[num].typeAttr=flags;
}

