#include "getopt_cpp.h"
#include <stdio.h>
#include <string.h>

/*
  constructor
 */
GetOptLong::GetOptLong(int _argc, char *const _argv[], const char *_optstring, const GetOptLong::option * _longopts) :
    opterr(0),
    optind(1),
    optopt(0),
    longindex(-1),
    optarg(NULL),
    argc(_argc),
    argv(_argv),
    optstring(_optstring),
    longopts(_longopts),
    place("")
{}

/*
  main parse code
 */
int GetOptLong::getoption(void)
{
    const char        *oli;          /* option letter list index */

    if (!*place)
    { /* update scanning pointer */
        if (optind >= argc)
        {
            place = "";
            return -1;
        }

        place = argv[optind];

        if (place[0] != '-')
        {
            place = "";
            return -1;
        }

        place++;

        if (place[0] && place[0] == '-' && place[1] == '\0')
        { /* found "--" */
            ++optind;
            place = "";
            return -1;
        }

        if (place[0] && place[0] == '-' && place[1])
        {
            /* long option */
            size_t namelen;
            int    i;

            place++;

            namelen = strcspn(place, "=");
            for (i = 0; longopts[i].name != NULL; i++)
            {
                if (strlen(longopts[i].name) == namelen
                    && strncmp(place, longopts[i].name, namelen) == 0)
                {
                    if (longopts[i].has_arg)
                    {
                        if (place[namelen] == '=')
                            optarg = place + namelen + 1;
                        else if (optind < argc - 1)
                        {
                            optind++;
                            optarg = argv[optind];
                        }
                        else
                        {
                            if (optstring[0] == ':')
                                return BADARG;
                            if (opterr)
                                fprintf(stderr,
                                        "%s: option requires an argument -- %s\n",
                                        argv[0], place);
                            place = "";
                            optind++;
                            return BADCH;
                        }
                    }
                    else
                    {
                        optarg = NULL;
                        if (place[namelen] != 0)
                        {
                            /* XXX error? */
                        }
                    }

                    optind++;

                    longindex = i;

                    place = "";

                    if (longopts[i].flag == NULL)
                        return longopts[i].val;
                    else
                    {
                        *longopts[i].flag = longopts[i].val;
                        return 0;
                    }
                }
            }

            if (opterr && optstring[0] != ':')
                fprintf(stderr,
                        "%s: illegal option -- %s\n", argv[0], place);
            place = "";
            optind++;
            return BADCH;
        }
    }

    /* short option */
    optopt = (int) *place++;

    oli = strchr(optstring, optopt);
    if (!oli)
    {
        if (!*place)
            ++optind;
        if (opterr && *optstring != ':')
            fprintf(stderr,
                    "%s: illegal option -- %c\n", argv[0], optopt);
        return BADCH;
    }

    if (oli[1] != ':')
    { /* don't need argument */
        optarg = NULL;
        if (!*place)
            ++optind;
    }
    else
    { /* need an argument */
        if (*place) /* no white space */
            optarg = place;
        else if (argc <= ++optind)
        { /* no arg */
            place = "";
            if (*optstring == ':')
                return BADARG;
            if (opterr)
                fprintf(stderr,
                        "%s: option requires an argument -- %c\n",
                        argv[0], optopt);
            return BADCH;
        }
        else
            /* white space */
            optarg = argv[optind];
        place = "";
        ++optind;
    }
    return optopt;
}

