                                          mImpl.mTportName, 
                                          mImpl.mSourceName,
                                          mImpl.mBridge);
        mImpl.mSource->getTransport()->setOutboundThrottle (
                                        mImpl.mThrottleRate, 
                                        MAMA_THROTTLE_DEFAULT);
    }    
    return mImpl.mSource;
}

